import React, { ReactElement } from "react";
import styled from "styled-components/native";
import { View, H1, Button, Text } from "../../styles";
import { baseURL } from "../../utils/request";

interface Props {
  children: string;
  image: string;
}

const HomeCardDiv = styled(View)`
  background-color: #333;
  border-radius: 5px;
  height: 60px;
  margin-top: 10px;
  margin-bottom: 10px;
  margin-left: 20px;
  margin-right: 20px;
  color: ${(props: any) => props.theme.text};
  flex-direction: row;
  align-items: center;
  font-size: 32px;
  padding: 10px;
`;

const Image = styled.Image`
  width: 40;
  height: 40;
  margin-right: 20px;
  enterhandler
`;

export default function HomeCard({ children, image }: Props): ReactElement {
  return (
    <HomeCardDiv>
      <Image
        // source={require("../../assets/favicon.png")}
        source={{
          uri: `${baseURL}/${image}`,
        }}
      />
      {children}
    </HomeCardDiv>
  );
}
