import React, { ReactElement, useState } from "react";
import { TouchableOpacity } from "react-native";
import useInputState from "../../hooks/useInputState";
import { POST } from "../../utils/request";

import {
  AuthInput,
  AuthButton,
  AuthSwitch,
  AuthSwitchText,
  AuthDiv,
  AuthH1,
  AuthTop,
  AuthBottom,
  P,
  Error,
} from "./Auth.styles";

export default function Login({ navigation }: any): ReactElement {
  const [username, setUsername, resetUsername] = useInputState();
  const [password, setPassword, resetpassword] = useInputState();

  const [error, setError] = useState(false);

  const handleSubmit = async () => {
    if (!username || !password) {
      setError(true);
    }

    const { error } = await POST("/auth/login", { username, password });

    if (!error) {
    }
  };

  return (
    <AuthDiv>
      <AuthTop>
        <AuthH1>Log in</AuthH1>
        <P>Login to your account to access the community</P>
      </AuthTop>
      <AuthBottom>
        <AuthInput
          onChangeText={setUsername}
          placeholder="Username"
          value={username}
        />
        <AuthInput
          onChangeText={setPassword}
          placeholder="Password"
          value={password}
          secureTextEntry={true}
        />
        <AuthSwitch>
          <AuthSwitchText>
            Don't have an account?
            <TouchableOpacity
              style={{ marginLeft: 5 }}
              onPress={() => navigation.navigate("Register")}
            >
              Register
            </TouchableOpacity>
          </AuthSwitchText>
        </AuthSwitch>
        <TouchableOpacity disabled={error} onPress={handleSubmit}>
          <AuthButton disabled={error}>Submit</AuthButton>
        </TouchableOpacity>
      </AuthBottom>
    </AuthDiv>
  );
}
