import React, { ReactElement } from "react";
import { TouchableRipple } from "react-native";
import styled from "styled-components/native";
import useInputState from "../../hooks/useInputState";

import { View, H1, Button, Text } from "../../styles";
import {
  AuthInput,
  AuthButton,
  AuthSwitch,
  AuthSwitchText,
} from "./Auth.styles";

export const LoginDiv = styled(View)`
  flex: 1;
`;

export const LoginTop = styled(View)`
  padding: 30px;
  flex: 1;
`;

export const LoginBottom = styled(View)`
  flex: 2;
  background-color: ${(props: any) => props.theme.text};
  border-top-left-radius: 75px;
  padding-top: 40px;
  padding-left: 40px;
  padding-right: 40px;
  padding-bottom: 20px;
`;

export const LoginH1 = styled(H1)`
  font-size: 60;
`;

export const P = styled(Text)`
  opacity: 0.7;
  margin-top: 25px;
  font-size: 22;
`;

export default function Login(): ReactElement {
  const [username, setUsername, resetUsername] = useInputState();
  const [password, setPassword, resetpassword] = useInputState();

  const handleSubmit = () => {};

  return (
    <LoginDiv>
      <LoginTop>
        <LoginH1>Log in</LoginH1>
        <P>Login to your account to access the community</P>
      </LoginTop>
      <LoginBottom>
        <AuthInput
          onChangeText={(text: string) => setUsername(text)}
          placeholder="Username"
        />
        <AuthInput
          onChangeText={(text: string) => setPassword(text)}
          placeholder="Password"
        />
        <AuthSwitch>
          <AuthSwitchText>Don't have an account? Register</AuthSwitchText>
        </AuthSwitch>
        <Touchable onPress={handleSubmit}>
          <AuthButton>Submit</AuthButton>
        </Touchable>
      </LoginBottom>
    </LoginDiv>
  );
}
